import Slider from "react-slick";
import Container from "../other/Container";
import Product from "../product/latestProduct";

import { Empty } from "antd";
import { useSelector } from "react-redux";
import { getProductsByFilter } from "../../common/shopUtils";
import { useState, useEffect } from "react";

const arrowStyle = {
  display: "flex",
  alignItems: "center",
  cursor:"pointer"
};

function SampleNextArrow({ onClick }) {
  return (
    <div style={arrowStyle} onClick={onClick}>
      Next
    </div>
  );
}

function SamplePrevArrow({ onClick }) {
  return (
    <div style={arrowStyle} onClick={onClick}>
      Prev
    </div>
  );
}

function ShopContentProduct({ data }) {
  const shopState = useSelector((state) => state.shopReducer);
  const globalState = useSelector((state) => state.globalReducer);
  const [currentData, setCurrentData] = useState();
  const [page, setPage] = useState(1);
  const [offset, setOffset] = useState(0);
  useEffect(() => {
    let filteredProduct = getProductsByFilter(
      [...data],
      shopState.sort,
      shopState.subCategory
    );
    setCurrentData(filteredProduct);
    setOffset(0);
  }, [shopState, data]);
  useEffect(() => {
    setPage(1);
  }, [globalState]);

  const settings = {
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    rtl: true,
    arrows: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 6,
    autoplay: false,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 920,
        settings: {
          slidesToShow: 4,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 2,
        },
      },
    ],
  };
  return (
    <div className="shop-content__product">
      {!currentData ? (
        <Empty description="No products in this category" />
      ) : (
        <>
          {currentData.length > 0 ? (
            <>
              <Container>
              <h2 style={{"margin":"20px"}}>Latest</h2>
              <hr />
                <Slider {...settings} style={{ display: "flex" }}>
                  {currentData.slice(0, 7).map((product, index) => (
                    <div key={index} className="slider-item">
                      <Product data={product} />
                    </div>
                  ))}
                </Slider>
              </Container>
            </>
          ) : (
            <Empty />
          )}
        </>
      )}
    </div>
  );
}

export default React.memo(ShopContentlatestProduct);
