import Slider from "react-slick";
import Container from "../../components/other/Container";
import Product from "../../components/product/Product";

import { Empty } from "antd";
import { useSelector } from "react-redux";
import { getProductsByFilter } from "../../common/shopUtils";
import { useState, useEffect } from "react";



function ShopContentProduct({ data }) {
  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "red" }}
        onClick={onClick}
      />
    );
  }
  
  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "green" }}
        onClick={onClick}
      />
    );
  }
  const shopState = useSelector((state) => state.shopReducer);
  const globalState = useSelector((state) => state.globalReducer);
  const [currentData, setCurrentData] = useState();
  const [page, setPage] = useState(1);
  const [offset, setOffset] = useState(0);
  useEffect(() => {
    let filteredProduct = getProductsByFilter(
      [...data],
      shopState.sort,
      shopState.subCategory
    );
    setCurrentData(filteredProduct);
    setOffset(0);
  }, [shopState, data]);
  useEffect(() => {
    setPage(1);
  }, [globalState]);
 
  const settings = {
    arrow:true,
    infinite: true,
    slidesToShow: 6,
    slidesToScroll: 1,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
    responsive: [
      {
        breakpoint: 920,
        settings: {
          slidesToShow: 4,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 2,
        },
      },
    ],
  };
  return (
    <div className="shop-content__product">
      {!currentData ? (
        <Empty description="No products in this category" />
      ) : (
        <>
          {currentData.length > 0 ? (
            <>
              <Container>
                <Slider {...settings}>
                  {currentData.slice(0, 7).map((product, index) => (
                    <div key={index} className="slider-item" >
                      <Product data={product} />
                    </div>
                  ))}
                </Slider>
              </Container>
            </>
          ) : (
            <Empty />
          )}
        </>
      )}
    </div>
  );
}

export default React.memo(ShopContentProduct);
