import Carousel from 'react-material-ui-carousel'
import { makeStyles } from '@material-ui/core'
import bannerData from '../../data/slider.json';
import Container from "../other/Container";


const useStyle = makeStyles({
  container: {
    width:'auto'
  },
  image: {
    width: '100%',
    
    height: 280,
  }
})




export default function Banners({ containerType }) {
  const classes = useStyle();
  return (
    <div className="banners">
      <Container type={containerType}>
      <Carousel
      autoPlay={true}
      animation="fade"
      indicators={false}
      navButtonsAlwaysVisible={true}
      cycleNavigation={true}
      className={classes.container}
      StylesProvider
      navButtonsProps={{
        style: {
          color: '#494949',
          backgroundColor: '#FFFFFF',
          borderRadius: 0,
          margin: 0,
          width: 50,
        }
      }}
    >
      {
        bannerData[0].images.map(image => (
          <img src={image} className={classes.image} />
        ))
      }
    </Carousel>
        {/* <Row gutter={30}>
          {Array.from({ length: 3 }, (item, index) => (
            <Col key={index} className="gutter-row" span={24} sm={8}>
              <Link href={process.env.PUBLIC_URL + "#"}>
                <a className="banner-item">
                  <img
                    src={
                      process.env.PUBLIC_URL +
                      `/assets/images/banners/${index + 1}.png`
                    }
                    alt="banner"
                  />
                </a>
              </Link>
            </Col>
          ))}
        </Row> */}
      </Container>
    </div>
  );
}
