import { Select } from "antd";
import Link from "next/link";
import { useSelector, useDispatch } from "react-redux";
import React from "react";

import {
    setGlobalLanguage,
    setGlobalCurrency,
} from "../../../redux/actions/globalActions";
import Container from "../../other/Container";

function TopNav({ containerType }) {
    const { Option } = Select;
    const dispatch = useDispatch();
    const globalState = useSelector((state) => state.globalReducer);
    const onSelectLanguage = (value) => {
        dispatch(setGlobalLanguage(value));
    };
    const onSelectCurrency = (value) => {
        dispatch(setGlobalCurrency(value));
    };
    return (
        <div className="top-nav">
            <Container type={containerType}>
                <div className="top-nav-wrapper">
                    <div className="top-nav-links">
                        <div className="top-nav-links__item">
                            <Link href={process.env.PUBLIC_URL + "/#"}>
                                <a>
                                    <i className="icon_phone" />
                                    +8801711-223344
                                </a>
                            </Link>
                        </div>
                        <div className="top-nav-links__item">
                            <Link href={process.env.PUBLIC_URL + "/#"}>
                                <a>
                                    <i className="icon_mail" /> admin@admin.com
                                </a>
                            </Link>
                        </div>
                    </div>
                    {/* <div className="top-nav-selects">
            <Select
              defaultValue={globalState.language}
              style={{ width: 90 }}
              bordered={false}
              onChange={onSelectLanguage}
            >
              <Option value="en">English</Option>
              <Option value="bn">Bangla</Option>
            </Select>
            <Select
              defaultValue={globalState.currency.currency}
              style={{ width: 120 }}
              bordered={false}
              onChange={onSelectCurrency}
            >
              <Option value="USD">USD - Dollar</Option>
              <Option value="BDT">BDT - Taka</Option>
            </Select>
          </div> */}
                </div>
            </Container>
        </div>
    );
}

export default React.memo(TopNav);
