  const settings = {
    rtl:true,
    arrows: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 6,
    autoplay: true,
    autoplaySpeed: 2000,
    responsive: [
      {
        breakpoint: 920,
        settings: {
          slidesToShow: 4,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 2,
        },
      },
    ],
  };
  return (
    <div className="shop-content__product">
      {!currentData ? (
        <Empty description="No products in this category" />
      ) : (
        <>
          {currentData.length > 0 ? (
            <>
              <Container>
                <Slider {...settings} >
                  {currentData.slice(0, 7).map((product, index) => (
                    <div key={index} className="slider-item">
                      <Product data={product} />
                    </div>
                  ))}
                </Slider>
              </Container>
            </>
          ) : (
            <Empty />
          )}
        </>
      )}
    </div>
  );
}

export default React.memo(ShopContentProduct);
