import React from "react";
import { Menu, Select } from "antd";
import { useSelector, useDispatch } from "react-redux";
import Link from "next/link";

import {
  setGlobalLanguage,
  setGlobalCurrency,
} from "../../../redux/actions/globalActions";

function MenuSidebar() {
  const { SubMenu } = Menu;
  const { Option } = Select;
  const dispatch = useDispatch();
  const globalState = useSelector((state) => state.globalReducer);
  const onSelectLanguage = (value) => {
    dispatch(setGlobalLanguage(value));
  };
  const onSelectCurrency = (value) => {
    dispatch(setGlobalCurrency(value));
  };
  return (
    <div className="menu-sidebar">
      <Menu mode="inline">
        <SubMenu key="sub2" title="Shop">
      
          <Menu.Item key="8">
            <Link href={process.env.PUBLIC_URL + "/shop/checkout"}>
              <a>Checkout</a>
            </Link>
          </Menu.Item>
          <Menu.Item key="9">
            <Link href={process.env.PUBLIC_URL + "/shop/checkout-complete"}>
              <a>Checkout Complete</a>
            </Link>
          </Menu.Item>
        </SubMenu>
        <Menu.Item key="10">
          <Link href={process.env.PUBLIC_URL + "#"}>
            <a>+8801711-223344</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="11">
          <Link href={process.env.PUBLIC_URL + "#"}>
            <a>admin@admin.com</a>
          </Link>
        </Menu.Item>
      </Menu>
      <div className="menu-sidebar-selects">
        <Select
          defaultValue={globalState.language}
          style={{ width: 120 }}
          bordered={false}
          onChange={onSelectLanguage}
        >
          <Option value="en">English</Option>
          <Option value="bn">Bangla</Option>
        </Select>
        <Select
          defaultValue={globalState.currency.currency}
          style={{ width: 150 }}
          bordered={false}
          onChange={onSelectCurrency}
        >
          <Option value="USD">USD - Dollar</Option>
          <Option value="BDT">BDT - Taka</Option>
        </Select>
      </div>
    </div>
  );
}

export default React.memo(MenuSidebar);
