import React, { useState } from "react";
import Link from "next/link";
import { Button, Drawer } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { CloseOutlined } from "@ant-design/icons";

import productsData from "../../../data/product.json";
import CartSidebar from "../../cart/CartSidebar";
import WishlistSidebar from "../../wishlist/WishlistSidebar";
import MenuSidebar from "./MenuSidebar";
import SearchBar from "./SearchBar";
import { getTotalProductInCart } from "../../../common/shopUtils";
import Container from "../../other/Container";

function Menu({ containerType }) {
  const cartState = useSelector((state) => state.cartReducer);
  const [cartSidebarOpen, setCartSidebarOpen] = useState(false);
  const [menuSidebarOpen, setMenuSidebarOpen] = useState(false);
  return (
    <>
      <div className="menu">
        <Container type={containerType}>
          <div className="menu-wrapper">
            <a
              href="#"
              className="menu-sidebar-opener"
              onClick={(e) => {
                e.preventDefault();
                setMenuSidebarOpen(true);
              }}
            >
              <div></div>
              <div></div>
              <div></div>
            </a>
            <div className="menu-logo">
              <Link href={process.env.PUBLIC_URL + "/"}>
                <a>
                 <h2 style={{color:'white'}}>Elementronics</h2>
                </a>
              </Link>
            </div>
            <SearchBar
              fillData={productsData}
              placeholder="What are you looking for ?"
            />
            <div className="menu-functions">
              <div className="menu-function-item">
                <Link
                  href={process.env.PUBLIC_URL + "/"}
                >
                  <img className="icon-color"
                    src={
                      process.env.PUBLIC_URL +
                      "/assets/images/header/home.svg"
                    }
                    alt=""
                  />
                </Link>
              </div>
              <div
                className="menu-function-item"
              >
                <img
                  src={
                    process.env.PUBLIC_URL +
                    "/assets/images/header/message.svg"
                  }
                  alt=""
                />
              </div>
              <div
                className="menu-function-item"
                onClick={() => setCartSidebarOpen(true)}
              >
                <img
                  src={
                    process.env.PUBLIC_URL +
                    "/assets/images/header/shopping-cart.svg"
                  }
                  alt=""
                />
                <span>{getTotalProductInCart(cartState)}</span>
              </div>
              <div
                className="menu-function-item"
              >
                <img
                  src={
                    process.env.PUBLIC_URL +
                    "/assets/images/header/user.svg"
                  }
                  alt=""
                />
              </div>
            </div>
          </div>
        </Container>
      </div>
      <div className="menu-mobile-search">
        <Container>
          <SearchBar fillData={productsData} placeholder="Searching..." />
        </Container>
      </div>
    
      <Drawer
        placement="right"
        title={`Shopping cart (${getTotalProductInCart(cartState)})`}
        closable={true}
        onClose={() => setCartSidebarOpen(false)}
        closeIcon={
          <>
            <p>Close</p> <CloseOutlined />
          </>
        }
        visible={cartSidebarOpen}
        width={445}
        className="menu-side"
      >
        <CartSidebar />
      </Drawer>
      <Drawer
        placement="right"
        closable={true}
        title=" "
        onClose={() => setMenuSidebarOpen(false)}
        closeIcon={
          <>
            <p>Close</p> <CloseOutlined />
          </>
        }
        visible={menuSidebarOpen}
        width={350}
        className="menu-side"
      >
        <MenuSidebar />
      </Drawer>
    </>
  );
}

export default React.memo(Menu);
