import { Row, Col, Empty, Pagination } from "antd";
import { useSelector } from "react-redux";
import classNames from "classnames";

import Product from "../product/Product";
import {
  getProductsByFilter,
  getProductsBySearch,
} from "../../common/shopUtils";
import { useState, useEffect } from "react";

function ShopContentProduct({
  productResponsive,
  fiveColumn,
  data,
  productPerPage,
  productStyle,
}) {
  const shopState = useSelector((state) => state.shopReducer);
  const globalState = useSelector((state) => state.globalReducer);
  const [currentData, setCurrentData] = useState();
  const [page, setPage] = useState(1);
  const [offset, setOffset] = useState(0);
  useEffect(() => {
    let filteredProduct = getProductsByFilter(
      [...data],
      shopState.sort,
      shopState.subCategory
    );
    setCurrentData(filteredProduct);
    setOffset(0);
  }, [shopState, data]);
  useEffect(() => {
    setPage(1);
  }, [globalState]);

  // const itemRender = (current, type, originalElement) => {
  //   if (type === "prev") {
  //     return (
  //       <a>
  //         <i className="fal fa-angle-left" />
  //       </a>
  //     );
  //   }
  //   if (type === "next") {
  //     return (
  //       <a>
  //         <i className="fal fa-angle-right" />
  //       </a>
  //     );
  //   }
  //   return originalElement;
  // };

  // const onChangeOffset = (page, pageSize) => {
  //   let offset = (page - 1) * pageSize;
  //   setPage(page);
  //   setOffset(offset);
  // };
  const settings = {
    
        arrows: true,
        infinite: true,
        speed: 1000,
        slidesToShow: 6,
        autoplay: true,
        autoplaySpeed: 2000,
        responsive: [
          {
            breakpoint: 920,
            settings: {
              slidesToShow: 4,
            },
          },
          {
            breakpoint: 768,
            settings: {
              slidesToShow: 3,
            },
          },
          {
            breakpoint: 576,
            settings: {
              slidesToShow: 2,
            },
          },
        ],
      };
  return (
    <div className="shop-content__product">
      {!currentData ? (
        <Empty description="No products in this category" />
      ) : (
        <>
          {currentData.length > 0 ? (
            <>
              <Row gutter={[{ xs: 5, sm: 5, xl: 15, xxl: 30 }, 30]}>
                {currentData
                  // .slice(offset, offset + productPerPage)
                  .slice(0,7)
                  .map((product, index) => (
                    <Col key={index}
                      className="slider-item"
                    >
                      <Product data={product} />
                 
                      
                    </Col>
                     // <Container>
                      //       <h5>Discount When Purchased Together</h5>
                      //       <div className="">
                      //         <Slider {...settings}>
                      //           {productData.slice(0, 8).map((item, index) => (
                      //             <div className="slider-item" key={index}>
                      //               <Product data={item} />
                      //             </div>
                      //           ))}
                      //         </Slider>
                      //       </div>
                      //     </Container>
                  ))}
              </Row>
              {/* {currentData.length >= productPerPage && (
                <Pagination
                  classNames="shop-content__product-pagination"
                  defaultCurrent={1}
                  current={page}
                  total={currentData.length}
                  pageSize={productPerPage}
                  itemRender={itemRender}
                  onChange={(page, pageSize) => onChangeOffset(page, pageSize)}
                />
              )} */}
            </>
          ) : (
            <Empty />
          )}
        </>
      )}
    </div>
  );
}

export default React.memo(ShopContentProduct);
