import React, { useState, useEffect } from "react";
import Link from "next/link";
import Container from "../../other/Container";

import { Select, Button, AutoComplete } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { useRouter } from "next/router";
import { SHOP } from "../../../common/defines";
import { getProductsByCategory } from "../../../common/shopUtils";
import {setGlobalCategory,setGlobalSearch} from "../../../redux/actions/globalActions";
import { setSubCategory } from "../../../redux/actions/shopActions";
import useDebounce from "../../../common/useDebound";

function Navbar({ containerType }) {
    const globalState = useSelector((state) => state.globalReducer);
  return (
    <div className="nav-menu">
      <Container type={containerType}>
        <div className="menu-wrapper">
          <div>
            <Link href={process.env.PUBLIC_URL + "/"}>
              <img
                className="icon-color"
                style={{ cursor: "pointer" }}
                src={
                  process.env.PUBLIC_URL + "/assets/images/header/square.svg"
                }
                alt=""
              />
              <Select
                className="menu-search__form-select"
                defaultValue={globalState.category}
                style={{ width: 220 }}
                onChange={onSelectCateory}
                value={globalState.category}
              >
                {SHOP.category.map((item, index) => (
                  <Option key={index} value={item.name}>
                    {item.name}
                  </Option>
                ))}
              </Select>
            </Link>
          </div>
          <div className="menu-functions">
            <div className="menu-function-item">
              <Link href={process.env.PUBLIC_URL + "/delivery"}>
                <h5 style={{ color: "white" }}>Delivery</h5>
              </Link>
            </div>
            <div className="menu-function-item">
              <Link href={process.env.PUBLIC_URL + "/help"}>
                <h5 style={{ color: "white" }}>Help</h5>
              </Link>
            </div>
            <div className="menu-function-item">
              <Link href={process.env.PUBLIC_URL + "/"}>
                <img
                  className="icon-color"
                  style={{ cursor: "pointer" }}
                  src={
                    process.env.PUBLIC_URL + "/assets/images/header/light.svg"
                  }
                  alt=""
                />
              </Link>
            </div>
          </div>
        </div>
      </Container>
    </div>
  );
}

export default Navbar;
